import { useState } from 'react';

interface UsersList {
    name: string;
    job: string;
    avatar: string;
    tags: string[];
}

const users = [
    {
        name: 'Joe Hamster',
        job: 'Software Engineer',
        avatar: 'data:image/jpeg;base64,',
        tags: ['React', 'NodeJS', 'Java'],
    },
    {
        name: 'Smith Marry',
        job: 'Software Engineer',
        avatar: 'data:image/jpeg;base64,',
        tags: ['React', 'NodeJS', 'Java'],
    },
    {
        name: 'Cue Wind',
        job: 'Software Engineer',
        avatar: 'data:image/jpeg;base64,',
        tags: ['React', 'NodeJS', 'Java'],
    },
    {
        name: 'Jessica Lee',
        job: 'Socials',
        avatar: 'data:image/jpeg;base64,',
        tags: ['Tiktok', 'Facebook'],
    },
];

export const UsersList = () => {
    const [query, setQuery] = useState('');

    const filterUsers =
        query.length > 0
            ? users.filter((user) => {
                  return user.name.toLowerCase().includes(query.toLowerCase());
              })
            : users;

    const TAGS: string[] = ['Profile', 'Deverloper', 'Editor', 'Person', 'Fashion'];

    return (
        <div className="mx-auto min-w-[40rem] rounded-lg bg-white p-16 shadow">
            <h1 className="text-2xl font-bold">User</h1>
            <div className="mt-4">
                {/* search */}
                <div className="flex items-center rounded-md border-2 border-[C4CADF]">
                    <svg
                        xmlns="http://www.w3.org/2000/svg"
                        fill="none"
                        viewBox="0 0 24 24"
                        stroke-width="1.5"
                        stroke="currentColor"
                        className="ml-4 size-6"
                    >
                        <path
                            stroke-linecap="round"
                            stroke-linejoin="round"
                            d="m21 21-5.197-5.197m0 0A7.5 7.5 0 1 0 5.196 5.196a7.5 7.5 0 0 0 10.607 10.607Z"
                        />
                    </svg>
                    <input
                        className="w-full p-4 outline-none"
                        onChange={(e) => setQuery(e.target.value)}
                        type="text"
                        placeholder="Search user..."
                    />
                </div>
                <div className="my-6 space-x-2">
                    {TAGS.map((tag) => (
                        <a
                            id={tag}
                            className="mt-4 flex-row rounded-md border-2 border-gray-300 px-4 py-3 transition-all hover:cursor-pointer hover:border-none hover:bg-[#b6d3f9] hover:text-white"
                        >
                            {tag}
                        </a>
                    ))}
                </div>
            </div>
            <div className="mt-16 grid grid-cols-1 gap-4 lg:grid-cols-3">
                {filterUsers.map((user) => (
                    <div className="flex gap-x-4 rounded-full px-4 py-2 hover:shadow-lg" key={user.name}>
                        <img
                            className="w-32 cursor-pointer self-start rounded-full border-[10px] border-[#E6EFFA]"
                            src={user.avatar}
                            alt={user.name}
                        />
                        <div className="mt-4 leading-relaxed">
                            <h2 className="text-xl font-bold">{user.name}</h2>
                            <h3 className="font-semibold">{user.job}</h3>
                            {/* tags */}
                            <div className="mt-2 flex cursor-pointer items-center justify-center gap-2">
                                {user.tags.map((tag) => (
                                    <span
                                        className="rounded-full border-2 border-gray-300 px-4 text-sm hover:border-none hover:bg-red-300 hover:text-white"
                                        key={tag}
                                    >
                                        {tag}
                                    </span>
                                ))}
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};
